mapper.update:

update SYSTEM_EDITOR
set ISSHOW = #{isshow,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select ID, NAME, DESCRIPTION, CREATER, CREATETIME, MODIFYER, LASTMODIFYTIME, ISSHOW, FILEURL, BASENAME, CONTENT from SYSTEM_EDITOR WHERE 1=1 AND ISSHOW='1'
${_parameter}
ORDER BY CREATETIME DESC
