mapper.select:

inner  JOIN SYSTEM_MENU b  ON b.ID= a.PARENT_ID
inner  JOIN SYSTEM_ROLE_MENU rm ON rm.PERMISSION_ID = a.id
inner  JOIN SYSTEM_ROLE r ON r.id = rm.ROLE_ID AND r.STATUS = '1'
inner  JOIN SYSTEM_USER_ROLE ur ON ur.ROLE_ID = r.id
inner  JOIN SYSTEM_USERS u ON u.id = ur.USER_ID AND u.STATUS='1' AND u.id = #{0}
WHERE a.STATUS = '1'
AND b.PERMISSION=#{1}
AND a.TYPE=#{2}
ORDER BY a.sort

mapper.update:
UPDATE SYSTEM_MENU
PARENT_ID = #{parentId}, name = #{name}, sort = #{sort}, type = #{type}, URL = #{url}, permission = #{permission}, icon = #{icon}, STATUS = #{status}, DESCRIPTION = #{description}, UPDATEDATE = #{updatedate}
WHERE ID = #{id}
